var curText = '';
terminalbox = document.getElementById('terminal');
terminalbox.classList.add('terminal');
var terminaltext = "#_";
terminalbox.innerHTML='<br><cursor id="cursor">#_</cursor>';
var terminal = TStart();
function TStart(){
return new App()
}
function App(){
this.log = function (texts,tag,color,font,size){
if(tag=='strong'){
const text = document.createElement('strong');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
else if(tag=='a'){
const text = document.createElement('a');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
else if(tag=='text'){
const text = document.createElement('text');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
else if(tag=='tt'){
const text = document.createElement('tt');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
else if(tag=='code'){
const text = document.createElement('code');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
else if(tag=='i'){
const text = document.createElement('i');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
else if(tag=='u'){
const text = document.createElement('u');
text.textContent=texts;
text.classList.add(color)
text.classList.add(font)
text.style='font-size:'+size+';';
terminalbox.appendChild(text)
terminalbox.scrollTop = terminalbox.scrollHeight;
}
}
this.space = function (){
terminalbox.innerHTML+='<br>';
}
this.loadScript= function(a,b,c){
const script = document.createElement('script');
script.type='text/javascript';
script.src=a;
script.onload = function (){
c()
}
script.onerror = function (){
b()
}
body = document.getElementById('body');
body.appendChild(script);
}
this.getText = function (func){
var entry = document.getElementById('txt').value;
return entry;
}
this.getLastText = function (){
	return curText;
}
this.onKey= function(func){
window.onkeypress=function(a){
func(a)
}
}
this.type = function(){
var input='<input type="text" id="txt">';
var input2 = terminalbox.innerHTML.replace(input,'');
terminalbox.innerHTML=input2;
terminalbox.innerHTML+=input;
}
this.text = function (){
return document.getElementById('txt').value;
}
this.clear = function (){
document.getElementById('terminal').innerHTML='<br><cursor id="cursor">'+terminaltext+'</cursor>';
}
this.cursorText = function (a){
var terminaltext = a;
document.getElementById('cursor').textContent=a;
};
this.pushCursor = function (){
Animate(250);
document.getElementById('cursor').style='color:#000';
var newa = terminalbox.innerHTML.replace('<br><cursor id="cursor" style="color: rgb(0, 0, 0);">'+terminaltext+'</cursor>','');
terminalbox.innerHTML=newa;
terminalbox.innerHTML+='<br><cursor id="cursor">'+terminaltext+'</cursor>';
Animate(250);
}
}

function Animate(dur){
setTimeout(on,dur)
function on(){
cursor = document.getElementById('cursor').style='color:green;';
setTimeout(off,dur)
}
function off(){
cursor = document.getElementById('cursor').style='color:#000';
setTimeout(on,dur)
}
}
function type(){
var input='<input type="text" id="txt">';
var input2 = terminalbox.innerHTML.replace(input,'');
terminalbox.innerHTML=input2;
terminalbox.innerHTML+=input;
}
function loadOS(a){
const script = document.createElement('script');
script.type='text/javascript';
script.src=a;
script.onload = function (){
onLoadSuccess()
}
script.onerror = function (){
onLoadError()
}
body = document.getElementById('body');
body.appendChild(script);
}